vec4 Effect2(vec2 uv) {
    // Normalize UV coordinates
    vec2 st = uv * 2.0 - 1.0;

    // Convert to polar coordinates
    float r = length(st);
    float a = atan2(st.y, st.x);

    // Create flower shape using rose curve equation
    float petalRadius = sin(a * 4.0 + iTime) * 0.5 + 0.5;
    float flowerMask = step(r, petalRadius * 0.8);

    // Calculate color based on angle relative to time
    vec3 hslColor = vec3((a / (pi * 2.0) + 0.5) * 360.0, 1.0, 1.0);
    vec3 petalColor = hsl2rgb(hslColor);

    // Add pulsating effect
    float pulse = sin(iTime * 0.5) * 0.2 + 0.8;
    petalColor *= pulse;

    // Create background color
    vec3 bgColor = vec3(0.1, 0.1, 0.2);
    bgColor += sin(iTime * 0.1) * 0.1;

    // Combine colors
    vec3 finalColor = mix(bgColor, petalColor, flowerMask);

    return vec4(finalColor, 1.0);
}
