vec4 Effect2(vec2 uv) {
    // Create rotating pattern with time
    vec2 st = uv * 10.0;
    float time = iTime * 0.5;

    // Polar coordinates for vortex effect
    float angle = atan2(st.y, st.x);
    float radius = length(st);

    // Add turbulence to create dynamic movement
    angle += turbulent(vec3(angle, radius, time)) * 0.5;
    radius += turbulent(vec2(radius, time)) * 0.3;

    // Create color transitions using HSL
    vec3 color = hsl(
        sin(time + angle) * 180.0,
        0.6 + noise(vec2(time, 0.0)) * 0.4,
        0.5 + sin(radius * 0.5 + time) * 0.5
    );

    // Add depth and layers
    float depth = noise(vec3(st.x*0.5, st.y*0.5, time)) * 0.2;
    color += hsl(
        sin(time + angle + PI) * 180.0,
        0.4,
        0.3 + noise(vec2(radius*0.2, time)) * 0.2
    ) * depth;

    return vec4(color, 1.0);
}
