vec4 Effect2(vec2 uv) {
    // Animate coordinates over time
    vec2 st = uv * 5.0 + sin(iTime * 0.5) * 0.5;

    // Create flowing pattern using turbulence
    float flow1 = turbulent(st + vec2(sin(iTime), cos(iTime))) * 0.5 + 0.5;
    float flow2 = noise(st * 0.1 + iTime) * 0.5 + 0.5;

    // Create rotating color wheel effect
    vec3 color1 = hsl(flow1 * 360.0, 0.7, 0.6);
    vec3 color2 = hsl((flow1 + flow2) * 360.0, 0.8, 0.5);

    // Combine patterns with color transformations
    vec3 finalColor = mix(color1, color2, flow2);
    finalColor *= sin(iTime) * 0.5 + 0.5;

    // Add noise variation
    float n = noise(st * 0.5 + iTime * 0.2);
    finalColor = mix(finalColor, vec3(n), 0.3);

    return vec4(finalColor, 1.0);
}
