vec4 Effect2(vec2 uv) {
    // Create base noise pattern
    float speed = 0.5;
    vec2 st = uv * 8.0; // Scale coordinates for more detail

    // Add movement over time
    st.x += sin(iTime * speed + st.y * 3.0) * 0.5;
    st.y += cos(iTime * speed + st.x * 3.0) * 0.5;

    // Compute turbulence
    float turb = turbulent(st);

    // Create color shifts based on noise and time
    vec3 color1 = hsl2rgb(vec3(0.6 + turb * 0.4, 0.8, 0.7));
    vec3 color2 = hsl2rgb(vec3(0.3 + turb * 0.4, 0.8, 0.7));

    // Mix colors based on position and time
    float mixAmount = sin(iTime * 0.5 + st.x) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, mixAmount);

    // Add depth effect
    finalColor *= 1.0 - length(uv) * 0.4;

    return vec4(finalColor, 1.0);
}
