vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for grid pattern
    vec2 st = uv * 10.0;

    // Create a grid of lines using sine waves
    float horizontal = sin(st.y + iTime * 0.5);
    float vertical = sin(st.x + iTime * 0.5);

    // Calculate distance field for glowing effect
    float glow = min(abs(horizontal), abs(vertical));
    glow = 1.0 - smoothstep(0.0, 0.3, glow);

    // Add turbulence to create organic movement
    vec2 noisePos = st * 0.5 + iTime;
    float turb = turbulent(noisePos) * 0.5 + 0.5;

    // Combine grid and turbulence for final pattern
    float pattern = mix(horizontal, vertical, turb);

    // Create color transitions using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(iTime * 0.1, 0.7, 0.5));

    // Add pulsating brightness effect
    float brightness = sin(iTime) * 0.5 + 0.5;
    color *= brightness;

    // Final output with glow and pattern
    return vec4(color * glow, 1.0);
}
