vec4 Effect2(vec2 uv) {
    // Base color for jungle background
    vec3 baseColor = hsl(120.0, 0.5, 0.3);

    // Add atmospheric mist effect using noise
    float mist = noise(uv * 0.5 + iTime * 0.1) * 0.5 + 0.5;
    vec3 mistColor = hsl(180.0, 0.3, 0.2);

    // Create tree trunks using noise
    float trunkNoise = noise(uv * 4.0 + iTime * 0.1);
    vec3 trunkColor = hsl(120.0, 0.7, 0.2);

    // Add leaves detail with turbulence
    float leafDetail = turbulent(uv * 8.0 + iTime) * 0.5 + 0.5;
    vec3 leafColor = hsl(120.0, 0.6, 0.4);

    // Combine elements
    vec3 finalColor = baseColor;
    finalColor = mix(finalColor, mistColor, mist * 0.4);
    finalColor = mix(finalColor, trunkColor, step(0.7, trunkNoise) * 0.5);
    finalColor = mix(finalColor, leafColor, step(0.8, leafDetail) * 0.3);

    // Add distance fog
    vec3 fogColor = hsl(120.0, 0.4, 0.1);
    float depth = uv.y;
    finalColor = mix(finalColor, fogColor, depth * 0.5);

    // Add dynamic highlights
    float highlight = turbulent(uv * 16.0 + iTime * 2.0) * 0.5 + 0.5;
    vec3 highlightColor = hsl(120.0, 0.8, 0.6);
    finalColor = mix(finalColor, highlightColor, step(0.9, highlight) * 0.2);

    return vec4(finalColor, 1.0);
}
