vec4 Effect2(vec2 uv) {
    // Create rotating pattern by adding time-based rotation
    vec2 st = uv * 10.0 + vec2(sin(iTime), cos(iTime)) * 5.0;

    // Use noise and turbulence to create complex patterns
    float t = turbulent(st);
    float n = noise(vec3(st, iTime));

    // Create color variations using HSL
    vec3 color1 = hsl2rgb(vec3(0.5 + t * 0.5, 0.7, 0.6));
    vec3 color2 = hsl2rgb(vec3(0.3 + n * 0.5, 0.8, 0.7));

    // Combine colors with smooth variation
    float blend = sin(iTime) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, blend);

    return vec4(finalColor, 1.0);
}
