vec4 Effect2(vec2 uv) {
    // Create base noise values
    float n = noise(uv);

    // Add turbulence for more dynamic movement
    vec3 pos3d = vec3(uv, iTime * 0.1);
    float turb = turbulent(pos3d);

    // Calculate color shifts based on time and position
    vec3 color = hsl2rgb(vec3(
        fract(iTime * 0.5 + n * 2.0) * PI,
        0.7, // Saturation
        0.6 // Lightness
    ));

    // Create floating particle effect
    vec2 movement = vec2(sin(iTime * 0.1 + uv.x * 3.0), cos(iTime * 0.2 + uv.y * 2.0)) * 0.5;
    vec2 finalUV = uv + movement;

    // Add depth and glow effect
    float dist = length(finalUV);
    color *= (1.0 - dist * 0.3) * (1.0 + turb * 0.5);

    return vec4(color, max(0.0, 1.0 - dist * 2.0 + n * 0.5));
}
