vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Add time-based rotation and some randomness
    a += iTime * 0.5 + noise(vec3(uv * 10.0, iTime)) * 0.2;
    // Create hexagonal pattern using polar coordinates
    float hexDistance = length(vec2(
        cos(a) * r,
        sin(a) * r
    ));
    // Add turbulence for glowing effect
    float glow = turbulent(vec3(uv * 10.0, iTime)) * 0.5 + 0.5;
    float pattern = hexDistance * (1.0 - glow);
    // Create vibrant color palette using HSL
    vec3 hslColor = vec3(
        mod(a * 6.0 + iTime, 2.0 * PI) / (2.0 * PI),
        0.7,
        0.5
    );
    vec3 rgbColor = hsl2rgb(hslColor);
    // Combine pattern and color
    vec3 finalColor = mix(
        vec3(0.0),
        rgbColor,
        smoothstep(0.8, 1.0, 1.0 - pattern)
    );
    return vec4(finalColor, 1.0);
}
