vec4 Effect2(vec2 uv) {
    // Calculate distance from center
    vec2 st = uv - 0.5;
    float radius = length(st);

    // Add rotation based on time
    float angle = iTime * 1.5;
    st.x = cos(angle)*st.x - sin(angle)*st.y;
    st.y = sin(angle)*st.x + cos(angle)*st.y;

    // Create expanding circles pattern
    radius += sin(iTime) * 0.3;
    float pattern = sin(radius * 12.0 + iTime * 2.0);

    // Generate color using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        (radius * 0.5 + pattern * 0.5) * PI,
        0.7 + turbulent(st * 10.0) * 0.3,
        0.5 + sin(iTime * 0.5) * 0.5
    ));

    // Add noise texture for extra detail
    vec2 noiseScale = st * 8.0;
    float noiseValue = turbulent(noiseScale);
    color *= 1.0 + noiseValue * 0.3;

    return vec4(color, 1.0);
}
