vec4 Effect2(vec2 uv) {
    vec4 result;

    // Create rotating pattern using time
    float angle = iTime * 0.5;
    mat2 rotMatrix = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotatedUV = uv * rotMatrix;

    // Generate turbulent noise for dynamic movement
    float turbulence = turbulent(rotatedUV * 5.0);

    // Create color transitions using HSL
    vec3 baseColor = hsl(0.5 + turbulence * 0.1, 1.0, 0.7);
    vec3 secondaryColor = hsl(0.4 + turbulence * 0.2, 1.0, 0.6);

    // Mix colors based on position and noise
    float blendFactor = length(uv) * 0.5;
    vec3 finalColor = mix(baseColor, secondaryColor, blendFactor);

    result = vec4(finalColor, 1.0);
    return result;
}
