vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 uvPolar = uv * 2.0 - 1.0;
    float r = length(uvPolar);
    float theta = atan(uvPolar.y, uvPolar.x);
    // Create galaxy spiral arms
    float spiral = mod(theta * 5.0 + iTime * 0.1, PI * 2.0);
    float armDistance = r - (sin(spiral) * 0.3 + 0.7);
    float galaxy = smoothstep(0.4, 0.6, abs(armDistance)) * 
                  noise(vec3(uvPolar * 10.0, iTime * 0.1));
    // Add central mass
    vec3 color = hsl2rgb(vec3(0.9, 0.8, 0.5)) * (1.0 - smoothstep(0.7, 1.0, r));

    // Add orbiting stars and planets
    for(float i = 0.0; i < 4.0; i++) {
        float angle = theta + iTime * 0.2 + i * PI * 0.5;
        vec2 starPos = vec2(cos(angle), sin(angle)) * (i * 0.3 + 0.2);
        float distance = length(uvPolar - starPos);

        // Create trails using noise and time
        float trail = turbulent(vec3(starPos * 10.0, iTime));
        color += hsl2rgb(vec3(i * 0.25, 0.7, 0.4)) * 
                smoothstep(0.1, 0.2, distance) * (1.0 + trail * 0.5);
    }
    // Add galaxy background
    color += hsl2rgb(vec3(0.8, 0.6, 0.2)) * galaxy;

    return vec4(color, 1.0);
}
