vec4 Effect2(vec2 uv) {
    // Create a pulsating pattern using time and UV coordinates
    float mirrors = 8.0 + sin(iTime * 0.5) * 4.0;
    vec2 grid = floor(uv * mirrors + 0.5);
    vec2 mirroredUV = fract(abs(grid - uv * mirrors));

    // Add turbulence for dynamic patterns
    float t = turbulent(mirroredUV * 10.0 + sin(iTime) * 3.0);

    // Create kaleidoscopic color pattern using HSL
    vec3 hslColor = vec3(
        fract(sin(grid.x + iTime) * 2.0),
        0.7 + t * 0.3,
        0.5 + sin(iTime * 0.8) * 0.5
    );

    // Convert to RGB and return as fragment color
    return vec4(hsl2rgb(hslColor), 1.0);
}
