vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for better coverage
    vec2 st = uv * 8.0 - 4.0;

    // Create base sine wave pattern with time-based pulsing
    float baseWave = sin(st.x * 0.5 + iTime) * 0.5 + 0.5;

    // Add secondary ripple effect using higher frequency sine waves
    float ripples = sin(st.x * 3.0 + iTime * 2.0) * 0.3;

    // Combine waves and add noise for organic feel
    float finalPattern = baseWave + ripples + turbulent(st * 0.5);

    // Create color transitions using HSL to RGB conversion
    vec3 color1 = hsl2rgb(vec3(0.0, 1.0, 0.5));
    vec3 color2 = hsl2rgb(vec3(0.6, 1.0, 0.5));

    // Mix colors based on pattern and time
    vec3 finalColor = mix(color1, color2, sin(iTime * 0.5) * 0.5 + 0.5);

    // Apply the pattern to the color
    finalColor *= finalPattern;

    return vec4(finalColor, 1.0);
}
