vec4 Effect2(vec2 uv) {
    // Base color - dark blue background
    vec3 baseColor = hsl(230.0, 0.1, 0.05);

    // Create island shapes using noise and turbulence
    float islandNoise = turbulent(uv * 8.0 + iTime * 0.1) * 0.5 + 0.5;
    vec3 islandColor = hsv2rgb(vec3(140.0, 0.6, islandNoise * 0.7));

    // Add fireflies with trails
    float decay = 0.98;
    vec3 glow = vec3(0.0);

    // Firefly 1
    vec2 pos1 = sin(vec2(iTime * 2.0, iTime * 3.0) + uv * 4.0) * 0.5 + 0.5;
    float dist1 = length(uv - pos1);
    glow += decay * exp(-dist1 * 8.0) * vec3(1.0, 0.8, 0.2);

    // Firefly 2
    vec2 pos2 = sin(vec2(iTime * 2.5 + pi, iTime * 4.0 - pi) + uv * 6.0) * 0.5 + 0.5;
    float dist2 = length(uv - pos2);
    glow += decay * exp(-dist2 * 8.0) * vec3(1.0, 0.8, 0.2);

    // Combine elements
    vec3 finalColor = mix(baseColor, islandColor, islandNoise * 0.5 + 0.3);
    finalColor += glow;

    return vec4(finalColor, 1.0);
}
