vec4 Effect2(vec2 uv) {
    vec3 finalColor = vec3(0.0);

    // Add base noise contribution
    float baseNoise = noise(uv * 10.0 + iTime);

    // Create particle swarm effect
    for(float i = 0.0; i < 10.0; i++) {
        // Generate random parameters for each particle
        vec2 pos = uv + vec2(sin(iTime + i*2.0), cos(iTime + i*3.0)) * 0.5;
        float phase = noise(vec3(pos, iTime)) * pi;

        // Sinusoidal movement pattern
        float x = sin(iTime + phase) * 0.4 + pos.x;
        float y = cos(iTime + phase * 2.0) * 0.4 + pos.y;

        // Calculate distance from current pixel to particle position
        vec2 particleUV = vec2(x, y);
        float dist = length(uv - particleUV);

        // Add glowing effect with turbulence
        float glow = max(0.0, 1.0 - dist * 4.0) * turbulent(vec3(particleUV, iTime)) * 0.5;

        // Color based on position and time
        vec3 color = hsv2rgb(vec3((i + baseNoise * 2.0) / 10.0, 1.0, 1.0));

        // Add to final color with trail effect
        finalColor += color * glow;
    }

    return vec4(finalColor, 1.0);
}
