vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Create rotating pattern with time offset
    theta += iTime * 0.5;

    // Add noise and turbulence for dynamic patterns
    float noise1 = noise(vec3(r*0.5 + iTime*0.2, theta, 0.0));
    float noise2 = noise(vec3(r*2.0 - iTime*0.3, theta*2.0, 0.0));

    // Create color shifts using HSL
    vec3 baseColor = hsl(theta * 0.5 + iTime * 0.1, 0.7, 0.6);
    vec3 finalColor = mix(baseColor,
                         hsl(theta * 0.5 + iTime * 0.2, 0.8, 0.7),
                         noise1 * 0.5 + 0.5);

    // Combine patterns and colors
    float pattern = sin(r * 4.0 - iTime) * 0.5 + 0.5;
    pattern *= sin(theta * 3.0 + iTime * 2.0) * 0.5 + 0.5;
    pattern += noise1 * 0.3 + noise2 * 0.2;

    // Final color transformation
    vec3 color = mix(finalColor,
                    hsl2rgb(vec3(theta*0.5, 0.6, 0.8)),
                    pattern);

    return vec4(color, 1.0);
}
