vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv - 0.5;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create color transitions using time and angle
    vec3 color = hsl(
        sin(theta + iTime) * 0.5 + 0.5,
        sin(iTime * 0.5 + theta * 4.0) * 0.5 + 0.5,
        0.7
    );
    // Add turbulence for dynamic patterns
    float turb = turbulent(vec3(r, theta, iTime * 0.1));

    // Create concentric circles with shifting colors
    vec3 finalColor = hsl2rgb(
        vec3(
            sin(iTime + r * 5.0) * 0.5 + 0.5,
            cos(iTime * 0.7 + theta * 4.0) * 0.5 + 0.5,
            0.6
        )
    );
    // Combine everything and output
    return vec4(finalColor * (1.0 + turb * 0.3), 1.0);
}
