vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 center = vec2(0.5);
    vec2 coord = uv - center;
    float theta = atan(coord.y, coord.x);
    float r = length(coord);
    // Create slow-moving wave distortion
    float time = iTime * 0.1;
    float waveX = sin(uv.x * 4.0 + time) * 0.2;
    float waveY = cos(uv.y * 4.0 - time) * 0.2;

    // Apply distortion to coordinates
    vec2 distortedUV = uv + vec2(waveX, waveY);

    // Calculate new polar coordinates after distortion
    coord = distortedUV - center;
    theta = atan(coord.y, coord.x);
    r = length(coord);
    // Create color variations based on angle and radius
    vec3 color1 = hsl(theta * 0.5 + time, 0.8, 0.6);
    vec3 color2 = hsl(theta * 0.7 - time, 0.7, 0.5);

    // Add turbulence for dynamic effects
    float turb = turbulent(vec3(r * 0.5, theta * 0.5 + time, 0.0)) * 0.3;
    vec3 finalColor = mix(color1, color2, turb);

    // Combine colors and apply light rays effect
    finalColor *= smoothstep(0.0, 1.0, r * 4.0 - 1.0) * 1.5;
    finalColor += vec3(turb * 0.3);
    return vec4(finalColor, 1.0);
}
