vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan(st.y, st.x);
    // Add time-based rotation and scaling
    theta += iTime * 0.5;
    r *= 0.5 + 0.5 * sin(iTime) + 0.2 * turbulent(vec2(r, theta));
    // Create geometric patterns using sine waves
    float pattern = sin(theta * 6.0 + iTime) * cos(r * 10.0);

    // Add noise and turbulence for dynamic effects
    vec2 noiseCoords = vec2(r, theta) * 5.0;
    float noiseValue = noise(noiseCoords + iTime);
    pattern += turbulent(noiseCoords + iTime * 0.5);
    // Convert to HSL and adjust colors based on mouse movement
    vec3 hslColor = vec3(
        fract(iTime * 0.2 + pattern),
        0.7 + 0.3 * noiseValue,
        0.5 + 0.5 * sin(r * 10.0 + iTime)
    );

    // Convert to RGB and add glow effect
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor *= 1.0 + 0.5 * noiseValue;
    return vec4(rgbColor, 1.0);
}
