vec4 Effect2(vec2 uv) {
    // Center the UV coordinates and scale them
    vec2 st = uv * 2.0 - 1.0;

    // Create polar coordinates from UV
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Calculate base color using HSL
    vec3 baseColor = hsl(theta * 0.5, 1.0, 0.5);

    // Add noise and turbulence to create variations
    float noiseVal = noise(vec2(st.x * 0.5 + iTime, st.y * 0.5));
    float turb = turbulent(vec3(st.x, st.y, iTime));

    // Combine multiple patterns with different rotations
    vec2 rotatedSt = vec2(
        cos(theta) * st.x - sin(theta) * st.y,
        sin(theta) * st.x + cos(theta) * st.y
    );

    // Create a second pattern layer
    float r2 = length(rotatedSt);
    theta += 1.5708; // Offset by 90 degrees

    // Mix colors using noise and turbulence
    vec3 color = mix(baseColor, hsl(r * 4.0 + iTime, 1.0, 0.5), noiseVal * 0.5 + 0.5);
    color = mix(color, hsv2rgb(vec3((r2 * 2.0 + iTime) * 0.2, 1.0, 1.0)), turb * 0.5 + 0.5);

    // Add depth and layers
    vec3 finalColor = mix(color, hsl(theta * 0.5, 1.0, 0.5), r * 0.2);

    return vec4(finalColor, 1.0);
}
