vec4 Effect2(vec2 uv) {
    // Create a rotating pattern by converting UV to polar coordinates
    vec2 pos = uv - 0.5;
    float radius = length(pos);
    float angle = atan2(pos.y, pos.x) + iTime * 0.5;

    // Generate noise patterns with different frequencies
    float n1 = noise(vec3(pos * 4.0 + iTime, 0.0)) * 0.5 + 0.5;
    float n2 = turbulent(vec3(pos * 8.0 + iTime * 2.0, 0.0)) * 0.5 + 0.5;

    // Combine noise layers with color transformations
    vec3 color1 = hsl2rgb(vec3(angle * 0.1, n1, 0.6));
    vec3 color2 = hsl2rgb(vec3((angle * 0.1) + 0.5, n2, 0.4));

    // Mix colors based on radius and time
    float mixAmount = sin(radius * 8.0 + iTime) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, mixAmount);

    return vec4(finalColor, 1.0);
}
