vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    float theta = atan2(uv.y, uv.x);
    float r = length(uv);

    // Add time-based rotation and turbulence
    vec3 p = vec3(uv.x, uv.y, iTime * 0.5);
    float t = turbulent(p * 0.5) * 0.5 + 0.5;

    // Create color transitions using HSL
    vec3 colorHSL = vec3(
        theta / (2.0 * PI) + t * 0.5,
        0.7 + sin(r * 10.0 + iTime) * 0.3,
        0.5 + cos(r * 10.0 + iTime) * 0.5
    );

    // Convert to RGB and return
    vec3 rgb = hsl2rgb(colorHSL);
    return vec4(rgb, 1.0);
}
