vec4 Effect2(vec2 uv) {
    // Create base noise values with time offset
    float t = iTime * 0.5;
    vec3 noise1 = vec3(noise(uv + vec2(t, t)), noise(uv + vec2(-t, t)), noise(uv + vec2(t, -t)));

    // Generate color using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        sin(t * 0.5) * 0.5 + 0.5,
        1.0,
        0.5
    ));

    // Add geometric pattern with turbulence
    float dist = length(uv - 0.5);
    float angle = atan2(uv.y - 0.5, uv.x - 0.5);
    float pattern = sin(dist * 10.0 + t) * cos(angle * 3.0 + t);

    // Combine noise and pattern for final color
    vec3 finalColor = mix(
        color,
        hsl2rgb(vec3(
            (sin(t) + 1.0) * 0.5,
            1.0,
            abs(pattern * 0.5 + 0.5)
        )),
        turbulent(uv * 4.0 + vec2(t)) * 0.5 + 0.5
    );

    return vec4(finalColor, 1.0);
}
