vec4 Effect2(vec2 uv) {
    // Create a noise-textured background
    vec3 baseColor = hsl2rgb(vec3(0.5, 1.0, 0.5));
    float noiseScale = 0.05;
    float timeScale = iTime * 0.1;
    vec2 noiseUV = uv * noiseScale + timeScale;
    float noiseValue = noise(noiseUV);

    // Create rotating geometric pattern
    vec2 center = uv - 0.5;
    float radius = length(center) * 3.0;
    float angle = atan2(center.y, center.x) + iTime;

    // Create pulsating color effect
    float hue = fract(iTime * 0.2);
    vec3 geometricColor = hsl2rgb(vec3(hue, 1.0, 0.5));

    // Combine noise and geometric patterns
    float pattern = sin(radius + angle) * 0.5 + 0.5;
    pattern *= turbulent(vec2(angle, radius)) * 0.5 + 0.5;

    // Overlay the geometric shapes on the noise background
    vec3 finalColor = mix(baseColor, geometricColor, pattern);

    return vec4(finalColor, 1.0);
}
