vec4 Effect2(vec2 uv) {
    // Create expanding circles pattern
    vec2 st = uv - 0.5;
    float d = length(st);

    // Add time-based expansion and noise
    float t = iTime * 0.1;
    float radius = mod(iTime, 20.0) * 0.1;
    float circle = smoothstep(radius - 0.1, radius + 0.1, d);

    // Create color with dynamic hue shift
    vec3 color = hsl2rgb(vec3(
        mod(d * 5.0 + iTime, 1.0),
        0.7,
        0.5
    ));

    // Add noise variation
    float noiseValue = turbulent(vec3(st * 4.0, t)) * 0.5;
    color *= 1.0 - noiseValue;

    return vec4(color, 1.0);
}
