vec4 Effect2(vec2 uv) {
    // Create time-based modulation
    float t = iTime * 0.5;

    // Add some noise to create interesting patterns
    vec3 noise1 = vec3(noise(uv + vec2(sin(t), cos(t))), 
                       noise(uv + vec2(cos(t), sin(t)) * 0.5),
                       noise(uv + vec2(0.0, t) * 0.7));

    // Create a color palette using HSL
    vec3 color1 = hsl(sin(t * 0.5) * 0.5 + 0.5, 
                      sin(t * 0.6) * 0.5 + 0.5,
                      sin(t * 0.7) * 0.5 + 0.5);

    // Create another color palette with phase shift
    vec3 color2 = hsl(sin(t * 0.5 + PI) * 0.5 + 0.5, 
                      sin(t * 0.6 + PI) * 0.5 + 0.5,
                      sin(t * 0.7 + PI) * 0.5 + 0.5);

    // Mix colors based on noise values
    vec3 finalColor = mix(color1, color2, noise1.x);

    // Add some distortion to the UV coordinates
    vec2 distortedUV = uv + vec2(sin(uv.y * 10.0 + t) * 0.1,
                                 cos(uv.x * 10.0 + t) * 0.1);

    // Create a light effect using noise and time
    float light = turbulent(vec3(distortedUV, t)) * 0.5 + 0.5;

    // Combine everything for the final effect
    return vec4(finalColor * light, 1.0);
}
