vec4 Effect2(vec2 uv) {
    // Center coordinates and scale
    vec2 st = (uv - 0.5) * 16.0;

    // Polar coordinates
    float angle = atan(st.y, st.x);
    float radius = length(st);

    // Create rotating pattern using sawtooth wave
    float pattern = mod(radius + iTime, 2.0);
    pattern = fract(pattern); 

    // Color based on angle and time
    vec3 color = hsl2rgb(vec3(
        mod(angle * 5.0 + iTime * 2.0, 1.0),
        0.7,
        0.8
    ));

    // Add noise for complexity
    float noiseValue = noise(st * 0.5 + vec2(iTime));
    color *= mix(1.0, 0.5, abs(noiseValue));

    // Create turbulence effect
    float turb = turbulent(st * 0.5 + vec2(iTime));
    pattern += turb * 0.3;

    // Final color with pattern influence
    color *= mix(vec3(1.0), vec3(0.0), abs(pattern - 0.5) * 2.0);

    return vec4(color, 1.0);
}
