vec4 Effect2(vec2 uv) {
    // Create rotating polar coordinates
    vec2 q = uv * 2.0 - 1.0;
    float radius = length(q);
    float angle = atan2(q.y, q.x) + iTime;

    // Add noise and turbulence effects
    float t1 = turbulent(vec3(radius, angle, iTime * 0.5));
    float t2 = turbulent(vec2(uv * 10.0 + iTime));

    // Create color from HSL values with varying hue and saturation
    vec3 color = hsl2rgb(vec3(
        angle * 0.15 + t1 * 0.5,
        0.7 + sin(radius * 5.0 + iTime) * 0.3,
        0.8 + t2 * 0.2
    ));

    // Combine effects and add pulsing
    float pulse = sin(iTime) * 0.5 + 0.5;
    return vec4(color * (t1 + t2) * 2.0, 1.0) * pulse;
}
