vec4 Effect2(vec2 uv) {
    // Create base noise pattern
    float n = noise(uv * 5.0 + iTime);

    // Add turbulence for more dynamic movement
    float t = turbulent(uv * 3.0 + vec2(iTime));

    // Combine noise and turbulence with color shifts
    vec3 c = hsl(
        0.7 + n * 0.1,   // Purple base with noise variation in hue
        1.0 - abs(n) * 0.5, // Vary saturation based on noise
        0.5 + t * 0.2    // Adjust lightness with turbulence
    );

    // Add time-based color shifting
    c = hsl2rgb(vec3(c.x + iTime * 0.1, c.y, c.z));

    // Create pulsating effect using distance from center
    float dist = length(uv);
    c *= 0.5 * (1.0 + sin(dist * 8.0 + iTime * 2.0));

    // Add swirling movement using rotated coordinates
    vec2 rotatedUv = uv * mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime));
    c += hsl(
        0.3 + n * 0.1,   // Blue base with noise variation in hue
        1.0 - abs(n) * 0.5,
        0.3 + t * 0.2
    ) * (0.5 + rotatedUv.x);

    return vec4(c, 1.0);
}
