vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create pulsating effect with time
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;

    // Add layered sine waves in radial direction
    float wave1 = sin(r * 8.0 + iTime) * 0.3;
    float wave2 = sin(r * 4.0 + iTime * 0.5) * 0.2;
    float wave3 = sin(r * 2.0 + iTime * 0.75) * 0.1;

    // Combine waves and add turbulence
    float noiseRadial = turbulent(vec2(r, a)) * 0.2;
    float noiseAngular = turbulent(vec2(a * 0.5, r * 0.5)) * 0.3;

    // Create color variations using HSL
    vec3 baseColor = hsl(r * 0.7 + iTime * 0.1, 0.6, 0.5);
    vec3 colorVariation = hsl(a * 0.5 + noiseRadial, 0.8, 0.6);

    // Combine all elements
    float finalRadius = r + wave1 + wave2 + wave3;
    float finalAngle = a + noiseAngular;

    // Generate the final color
    vec3 nebulaColor = mix(baseColor, colorVariation, noiseRadial * 0.5);
    nebulaColor *= (1.0 - abs(finalRadius - 1.0)) * pulse;

    // Add brightness and contrast adjustments
    nebulaColor = pow(nebulaColor, vec3(2.2));
    nebulaColor = mix(vec3(0.0), nebulaColor, 0.8);

    return vec4(nebulaColor, 1.0);
}
