vec4 Effect2(vec2 uv) {
    // Flowing liquid metal effect using noise and turbulence

    // Base noise values
    float n = noise(uv * 0.5 + iTime);
    float t = turbulent(uv * 3.0 + vec2(iTime*0.5, 0.0));

    // Color calculations
    vec3 color1 = hsl2rgb(vec3(0.6 + t*0.2, 0.8, 0.4)); // Blue-ish metal
    vec3 color2 = hsl2rgb(vec3(0.3 + t*0.3, 0.7, 0.5)); // Greenish reflection

    // Mix colors based on noise values
    vec3 finalColor = mix(color1, color2, n * 0.5 + 0.5);

    // Add flowing effect
    finalColor *= sin(uv.x * 4.0 + iTime) * 0.5 + 0.5;
    finalColor += vec3(turbulent(vec3(uv*2.0, iTime)))*0.3;

    return vec4(finalColor, 1.0);
}
