vec4 Effect2(vec2 uv) {
    // Base noise pattern for organic texture
    float noise1 = turbulent(uv * 8.0 + iTime);

    // Pulsation effect using sine wave
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;
    pulse += sin(iTime * 2.0) * 0.3;

    // Distance from center for radial patterns
    vec2 center = uv - 0.5;
    float dist = length(center);

    // Bioluminescent color transitions
    vec3 baseColor = hsl2rgb(vec3(0.6 + iTime * 0.1, 1.0, pulse));
    vec3 glowColor = hsl2rgb(vec3(0.8 + iTime * 0.15, 1.0, pulse * 0.7));

    // Create layered pulsation effect
    float layer1 = smoothstep(0.4, 0.6, dist) * baseColor.r;
    float layer2 = smoothstep(0.3, 0.5, dist) * glowColor.g;
    float layer3 = smoothstep(0.2, 0.4, dist) * (baseColor.b + glowColor.b);

    // Combine layers with noise modulation
    vec3 finalColor = mix(baseColor, glowColor, noise1 * 0.5 + 0.5);
    finalColor *= layer1 + layer2 + layer3;

    // Add emission effect
    finalColor += noise1 * 0.4 * glowColor;

    return vec4(finalColor, 1.0);
}
