vec4 Effect2(vec2 uv) {
    // Center the UV coordinates
    vec2 st = uv * 2.0 - 1.0;

    // Create a rotating pattern using polar coordinates
    float angle = atan2(st.y, st.x) + iTime * 0.5;
    float radius = length(st);

    // Add fractal noise to create complex patterns
    float noise1 = turbulent(vec3(st * 5.0, iTime * 0.1));
    float noise2 = turbulent(vec2(st * 10.0));

    // Create a radial gradient with pulsating effect
    float gradient = pow(1.0 - radius, 3.0) + sin(iTime * 0.5);

    // Combine noise and gradient for the final pattern
    vec3 color = hsl2rgb(vec3(
        fract(angle * 0.2 + iTime * 0.1),  // Hue variation over time and space
        0.7 + 0.3 * noise1,               // Saturation modulation
        gradient * (0.5 + 0.5 * noise2)    // Brightness with noise
    ));

    return vec4(color, 1.0);
}
