vec4 Effect2(vec2 uv) {
    // Animate UV coordinates over time
    vec2 st = uv * 5.0 + iTime;

    // Create displacement pattern using noise
    float displacement = turbulent(st * 0.5);

    // Calculate light direction and angle
    vec3 lightDir = vec3(sin(iTime) * 0.5, cos(iTime) * 0.5, 1.0);
    lightDir.z += 0.5;

    // Generate metallic color with shifting hues
    vec3 color = hsl(
        mod(length(st) + iTime * 0.2, 1.0), 
        0.7,
        0.4
    );

    // Apply noise-based intensity modulation
    float intensity = turbulent(vec3(uv * 5.0, iTime)) * 0.5 + 0.5;

    // Combine elements for final color
    return vec4(hsl2rgb(color) * intensity, 1.0);
}
