vec4 Effect2(vec2 uv) {
    // Rotate UV coordinates
    float t = iTime * 0.5;
    vec2 q = uv - 0.5;
    q = mat2(cos(t), sin(t), -sin(t), cos(t)) * q;

    // Create stripe pattern with sine waves and turbulence
    float v = sin(q.y * 10.0 + t) + turbulent(q.xy * 4.0 + vec2(sin(t), cos(t))) * 0.5;
    v = sin(v * pi + t) * 0.5 + 0.5;

    // Convert to HSL color with dynamic shifts
    vec3 c = hsl2rgb(vec3(
        sin(q.x * 5.0 + t) * 0.5 + 0.5,   // Hue variation
        1.0,                               // Full saturation
        v                                  // Intensity modulation
    ));

    return vec4(c, 1.0);
}
