vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to range [-1, 1]
    vec2 st = uv * 2.0 - 1.0;

    // Calculate distance from center
    float dist = length(st);

    // Create rotating pattern using cosine waves
    float theta = atan(st.y, st.x) + iTime * 0.5;
    float val = cos(theta * 4.0 + iTime) * 0.5 +
               cos(theta * 8.0 + iTime * 2.0) * 0.3 +
               cos(theta * 16.0 + iTime * 3.0) * 0.2;

    // Create color transitions
    vec3 color = hsl(
        sin(iTime * 0.5) * 0.5 + 0.5,   // Hue variation over time
        0.7,                             // Saturation
        (sin(dist * 4.0 + iTime) * 0.5 + 0.5) * 0.8 + 0.2  // Value variation based on distance and time
    );

    return vec4(color * (val + 3.0)/6.0, 0.7);
}
