vec4 Effect2(vec2 uv) {
    // Create rotating pattern using polar coordinates
    float angle = atan2(uv.y, uv.x) + iTime * 0.5;
    float radius = length(uv) * 2.0;

    // Add expansion/contraction effect
    radius += sin(iTime * 3.0) * 0.5;
    radius *= 0.7;

    // Create color variations using HSL
    vec3 color = hsl(angle * 0.1, 0.6 + noise(vec2(radius, iTime)) * 0.4, 0.8);

    // Add turbulence effect
    float turb = turbulent(vec2(uv.x * 5.0, uv.y * 5.0) + vec2(iTime));
    color *= 1.0 + turb * 0.3;

    // Mix in noise for additional texture
    float n = noise(vec2(radius, angle)) * 0.4;
    color += n;

    return vec4(hsl2rgb(color), 1.0);
}
