vec4 Effect2(vec2 uv) {
    // Animate position over time
    vec2 st = uv + sin(iTime * 0.5 + uv.xyy * 10.0).xy;

    // Create flowing noise pattern with multiple frequencies
    float n1 = noise(st * 3.0 + iTime);
    float n2 = noise(st * 6.0 + iTime * 0.7);
    float pattern = sin(n1 * 0.5 + n2) * 0.5 + 0.5;

    // Create color shifts using HSL
    vec3 colorHSL;
    colorHSL.x = (sin(iTime * 0.2 + st.x * 4.0) + sin(st.y * 3.0)) * 0.5 + 0.5;
    colorHSL.y = 0.7 + pattern * 0.3;
    colorHSL.z = 0.5 + turbulent(uv * 2.0) * 0.5;

    // Convert to RGB and return
    return vec4(hsl2rgb(colorHSL), 1.0);
}
