vec4 Effect2(vec2 uv) {
    // Rotate UV coordinates over time
    float angle = sin(iTime * 0.5);
    vec2 rotatedUV = cos(angle) * uv + sin(angle) * (uv.yx * vec2(1,-1));

    // Create base color using distance from center and time
    float dist = length(rotatedUV - 0.5);
    vec3 baseColor = hsl(dist * pi, 0.7, 0.5);

    // Add dynamic shifts to hue and saturation using turbulence
    float t1 = turbulent(rotatedUV * 10.0 + iTime) * 0.5;
    float t2 = turbulent(rotatedUV * 20.0 - iTime) * 0.3;

    vec3 color = hsl(
        baseColor.x + t1,
        baseColor.y + t2,
        0.5
    );

    return vec4(hsl2rgb(color), 1.0);
}
