vec4 Effect2(vec2 uv) {
    // Animate based on time
    float time = iTime * 0.5;

    // Create flowing liquid effect using noise and turbulence
    vec3 noisePos = vec3(uv * 2.0 + time, time);
    float flow = turbulent(noisePos * 0.5) * 0.5 + 0.5;

    // Add ripples with varying frequency
    vec2 rippleUV = uv * 4.0 + time * 0.1;
    float ripple = noise(rippleUV) * 0.3;

    // Combine effects for final position distortion
    vec2 distortedUV = uv + (flow + ripple) * 0.5;

    // Create color transitions using HSL
    vec3 color1 = hsl(time * 0.1, 0.7, 0.6);
    vec3 color2 = hsl((time * 0.1) + 0.5, 0.8, 0.4);

    // Mix colors based on noise
    float mixAmount = turbulent(vec3(uv * 2.0, time)) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, mixAmount);

    // Add metallic effect with highlights
    float highlight = noise((uv * 4.0) + time * 0.1) * 0.5 + 0.5;
    finalColor *= 1.0 + highlight * 0.3;

    return vec4(finalColor, 1.0);
}
