vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    float theta = atan2(uv.y, uv.x);
    float r = length(uv);

    // Create geometric pattern using sine waves in polar space
    vec3 baseColor = hsl(theta * 8.0 + iTime, 0.8, 0.5);

    // Add time-based distortion using noise and turbulence
    vec2 noiseUV = uv * 10.0 + iTime;
    float noiseValue = noise(noiseUV) * 0.5;
    float turbulentValue = turbulent(uv * 4.0 + iTime);

    // Mix colors with distortion
    vec3 color = mix(baseColor, hsv2rgb(vec3(theta * 0.1 + iTime, 1.0, 0.8)), noiseValue);
    color *= 1.0 + turbulentValue * 0.5;

    // Add radial variation
    color *= 1.0 - r * 0.5;
    color = mix(color, vec3(1.0), smoothstep(0.9, 1.0, r));

    return vec4(color, 1.0);
}
