vec4 Effect2(vec2 uv) {
    // Create a grid of hexagons based on time
    vec2 st = uv * 5.0 + iTime;

    // Transform coordinates for hexagonal tiling
    vec2 grid = floor(st);
    vec2 center = fract(st - 0.5);

    // Calculate distance from center with hexagonal warping
    float dist = length(center.x * cos(60.0) + center.y * sin(60.0));

    // Pulsating effect using sine wave
    float pulse = sin(iTime * 2.0 + dist * 10.0);

    // RGB color shift based on time and position
    vec3 color = hsl(dist * 0.5 + iTime, 0.7, 0.5);

    // Add radial gradient effect
    float gradient = smoothstep(0.0, 1.0, dist * 2.0 - 0.5);
    color *= mix(vec3(1.0), vec3(0.5), gradient);

    // Add turbulence for extra detail
    vec3 noiseColor = hsv2rgb(vec3(turbulent(uv * 4.0 + iTime) * 0.3, 0.7, 0.5));
    color = mix(color, noiseColor, 0.3);

    return vec4(color, 1.0);
}
