vec4 Effect2(vec2 uv) {
    // Create flowing sine wave patterns
    float time = iTime * 0.5;

    // X direction sine wave with turbulence
    float xSine = sin(uv.x * 10.0 + time) * 0.5 + 0.5;
    float xTurb = turbulent(vec2(uv.x * 5.0, time)) * 0.5 + 0.5;

    // Y direction sine wave with turbulence
    float ySine = sin(uv.y * 10.0 - time) * 0.5 + 0.5; 
    float yTurb = turbulent(vec2(time, uv.y * 5.0)) * 0.5 + 0.5;

    // Combine patterns
    float d = (xSine * xTurb) + (ySine * yTurb);
    d = mod(d * 10.0, 1.0); // Create interesting interference pattern

    // Dynamic color transitions using HSL
    vec3 hslColor;
    hslColor.x = mod(time * 0.2, 1.0); // Changing hue over time
    hslColor.y = 0.7; // Saturation
    hslColor.z = d * 0.5 + 0.5; // Lightness based on pattern

    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
