vec4 Effect2(vec2 uv) {
    // Scale and shift coordinates for larger pattern
    vec2 pos = uv * 8.0 + iTime;

    // Create base color using HSL
    vec3 baseColor = hsl(pos.x, 0.5, 0.5);

    // Add time-based animation
    float timeFactor = iTime * 0.1;

    // Use turbulence to create flowing pattern
    float turb2D = turbulent(pos + timeFactor);

    // Create secondary color using HSV conversion
    vec3 secondaryColor = hsv2rgb(vec3((pos.y + timeFactor) * 2.0, 0.7, 0.5));

    // Combine colors with noise modulation
    vec3 finalColor = mix(baseColor, secondaryColor, turb2D);

    return vec4(finalColor, 1.0);
}
