vec4 Effect2(vec2 uv) {
    // Create pulsating patterns using sine waves at different frequencies
    float time = iTime * 0.5;

    // Base pattern with UV coordinates transformed by time
    vec2 st = uv * 10.0 + vec2(sin(time), cos(time));

    // Add multiple layers of sine wave interference
    float pattern = sin(st.x * 0.5 + time) * 
                   sin(st.y * 0.5 + time * 0.7) +
                   sin(st.x * 1.5 - time * 0.3) *
                   sin(st.y * 1.5 + time * 0.5);

    // Add turbulent noise for extra complexity
    pattern += turbulent(vec2(uv * 8.0 + time)) * 0.5;

    // Create dynamic color shifts using HSL to RGB conversion
    vec3 color = hsl(
        sin(time) * 120.0,   // Hue variation over time
        0.7 + pattern * 0.3, // Saturation modulation
        0.5 + sin(time*0.5)*0.4 // Brightness pulsation
    );

    // Add depth with noise-based color shifts
    vec2 noiseCoords = uv * 16.0;
    float noiseVal = noise(noiseCoords);
    color += hsv2rgb(vec3(
        time * 180.0,
        0.5,
        noiseVal * 0.4
    )) * 0.3;

    // Combine patterns and colors for final output
    return vec4(color * (0.5 + sin(time*0.7) * 0.5), 1.0);
}
