vec4 Effect2(vec2 uv) {
    // Dynamic fiery color parameters with time
    float hue = sin(iTime * 0.5 + uv.x * pi) * 0.5 + 0.5;
    vec3 baseColor = hsl(hue, 0.8, 0.6);

    // Create circular pattern for glowing edges
    vec2 center = uv - 0.5;
    float dist = length(center);
    float glow = exp(-dist * 10.0) * (sin(iTime * 0.3 + dist * pi) * 0.5 + 0.5);

    // Add noise texture for detail
    vec2 noiseScale = uv * 8.0 + iTime;
    float noiseVal = turbulent(noiseScale) * 0.3;

    // Combine elements with smooth transitions
    vec3 finalColor = mix(baseColor, vec3(1.0), glow);
    finalColor *= sin(iTime * 0.2 + dist * pi) * 0.5 + 0.75;
    finalColor += noiseVal;

    return vec4(finalColor, 1.0);
}
