vec4 Effect2(vec2 uv) {
    // Transform UV coordinates to range -1.0 to 1.0
    vec2 st = uv * 2.0 - 1.0;

    // Add some movement based on time
    float time = iTime * 0.5;
    st += sin(time + st.x) * 0.3;

    // Create multiple light beams using noise and turbulence
    vec3 color = vec3(0.0);
    for(float i = 0.0; i < 8.0; i++) {
        float angle = (i + time) * PI * 0.25;
        vec2 pos = vec2(sin(angle), cos(angle)) * 0.7;

        // Calculate distance from beam center
        vec2 beamUV = uv - pos;
        beamUV.x *= 1.5; // Make beams more horizontal

        // Add turbulence for dynamic movement
        float turb = turbulent(beamUV * 4.0 + time);
        beamUV += noise(vec3(beamUV, time)) * 0.2;

        // Create circular gradient for beam shape
        float dist = length(beamUV) - 0.1;
        float gradient = smoothstep(0.0, 0.5, -dist + turb * 0.3);

        // Mix colors with HSL to RGB conversion
        vec3 beamColor = hsl2rgb(vec3((i + time) * 0.125, 0.8, 0.7));
        color += gradient * beamColor;
    }

    // Add some glow and vignette
    float glow = smoothstep(0.9, 1.0, length(st)) * 0.5;
    color *= (1.0 - glow) + 0.3;

    return vec4(color, 1.0);
}
