vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 centered = uv - 0.5;
    float r = length(centered);
    float theta = atan2(centered.y, centered.x);

    // Add time-based movement
    r += sin(theta + iTime * 0.5) * 0.1;
    theta += iTime * 0.2;

    // Create color based on radius and angle
    vec3 colorHSL = vec3(
        (r * 0.8 + 0.2 + sin(iTime) * 0.5), // Hue: varies with radius and time
        0.7, // Saturation
        0.5 + 0.4 * sin(theta * 3.0 + iTime) // Lightness: varies with angle and time
    );

    // Add noise for texture
    vec2 noiseUV = uv * 10.0 + iTime;
    float n = noise(noiseUV);

    // Combine color components with noise
    colorHSL.z += n * 0.2;

    // Convert to RGB and return
    return vec4(hsl2rgb(colorHSL), 1.0);
}
