vec4 Effect2(vec2 uv) {
    // Center coordinates around cursor position (0.5 is center)
    vec2 uv_cursor = uv - 0.5;

    // Scale coordinates for better ripple spacing
    uv_cursor *= 8.0;

    // Calculate distance from cursor
    float d = length(uv_cursor);

    // Create time-based ripple effect
    float ripples = sin(d * 2.0 + iTime) * 0.5 + 
                   sin(d * 4.0 + iTime * 2.0) * 0.3;

    // Add turbulence for more dynamic effect
    vec2 noise_coords = uv_cursor * 0.5 + iTime;
    float turb = turbulent(noise_coords);

    // Combine effects and scale to [0,1] range
    float final = (ripples + turb) * 0.5 + 0.5;

    // Convert to HSL color with time-based hue shift
    vec3 color = hsl2rgb(vec3(final * 0.7 + iTime, 0.8, 0.6));

    return vec4(color, 1.0);
}
